import { Injectable } from '@angular/core';

import { catchError, map } from 'rxjs/operators';
import { Observable, throwError, pipe } from 'rxjs';

import { HttpClient } from '@angular/common/http';
// import 'rxjs/add/operator/map';
// import { Observable } from 'rxjs/Observable';
import { GLOBAL } from './global';
import { Usuario } from '../models/usuario';
import { HttpHeaders } from '@angular/common/http';


// Decorador
@Injectable({
    providedIn: 'root'
  })
export class UsuarioServices{

    public url: string;

    // constructor( private _httpClient: HttpClient ){
    constructor( private _http: HttpClient ){
        this.url = GLOBAL.url;
    }

    async signUp(usuario_a_loguear: Usuario, gethash = false){
        
        try {
            if(gethash){
                usuario_a_loguear.gethash = gethash; 
            }
    
            console.log({usuario: usuario_a_loguear, message: "Usuario en usuarioServices:"});
    
            var email = usuario_a_loguear.email;
            var clave = usuario_a_loguear.clave;
    
            let json = JSON.stringify(usuario_a_loguear);
            let params = json;
    
            // Como usamos Js en el servidor tambien, definimos asi el Content-Type
            let headers = new HttpHeaders({
                'Content-Type':'application/json',
                'Authorization': 'Bearer clave_secreta_trabajo_final'
            });
    
            const options = { headers };
    
            console.log({json: json, message: "Usuario en Json:"});
            console.log( options);
            // console.log( "Params:" + params);
            console.log( this.url + 'loguear-usuario');
            
            // const { email, clave } = params;        
    
            //const response = await this._http.post(this.url + 'loguear-usuario', {email:email, clave:clave}, options );

            //console.log({response:response, message:"Respuesta luego de llamar al loguear-usuario"});
            // login(usuario_a_loguear.email: string, usuario_a_loguear.clave: string): Observable<any> {
            //     return this.http.post(this.url + 'loguear-usuario', params );
            // }
            // Devolvemos la petición AJAX
            var resp = await this._http.post( this.url + 'loguear-usuario', params ) //, { headers: headers })
                .pipe(catchError(error => {
                    console.log(error);
                    throw error;
                }))
                .toPromise();
                    // .pipe(
                    //     map( (response: any) =>{

                    //         //console.log({message: "Funciona?", resp:response.json()});
                            
                    //         response; //.json();
                    //     }),
                    //     catchError((error: any) => {
                    //         // Lógica de manejo de errores
                    //         console.error('Ocurrió un error al obtener el usuario:', error);
                    //         return throwError(() => new Error('Ocurrió un error al obtener el usuario:' + error))
                    //         })
                    // ); 
            
            console.log({message: "Funciona??", resp:resp});

            return resp;
                            
        } catch (error) {
            console.error('Ocurrió un error en el catch principal:', error);
            throw error
        }
        
        
    }
}
