import { Usuario } from './models/usuario';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent {
  public title = 'DistributionSystem';
  public usuario: Usuario;
  public identity = false; // Datos del usuario identificado ahi dentro. Guardado en localStorage
  public token = null;

  constructor(){
    this.usuario = new Usuario('','','','','ROLE_USER','', new Date, new Date);  
  }

}
