import { Injectable } from '@angular/core';

import { catchError, map } from 'rxjs/operators';
import { firstValueFrom, identity } from 'rxjs';

import { HttpClient, HttpParams } from '@angular/common/http';
import { GLOBAL } from './global';
import { Persona } from '../models/persona';
import { HttpHeaders } from '@angular/common/http';
import { UsuarioPersona } from '../models/usuarioPersona';

// Decorador
@Injectable({
    providedIn: 'root'
  })
export class PersonaServices{

    public url: string;
    public identity:any;
    public token:any;

    // constructor( private _httpClient: HttpClient ){
    constructor( private _http: HttpClient ){
        this.url = GLOBAL.url;
    }

    async guardarPersona(usuario_a_registrar: UsuarioPersona){
        
        let jsonUsuarioRegistro = JSON.stringify(usuario_a_registrar); 
        let params = jsonUsuarioRegistro;

        const data = { usuario_a_registrar };

        // Forma 2
        // var usuarioPr = new Persona('','','','','','');  
        // let jsonUsuarioRegistro = JSON.stringify(usuarioPr); 
        // let params = jsonUsuarioRegistro;

        // FORMA 3
        const body = new HttpParams()
            .set('email', usuario_a_registrar.correo_electronico)
            .set('nombre_usuario', usuario_a_registrar.nombre_usuario)
            .set('clave', usuario_a_registrar.password)
            .set('nombre', usuario_a_registrar.persona.nombre)
            .set('apellido', usuario_a_registrar.persona.apellido)
            .set('direccion', usuario_a_registrar.persona.direccion)
            .set('fecha_nacimiento', usuario_a_registrar.persona.fecha_nacimiento)
            .set('telefono', usuario_a_registrar.persona.telefono)       
            .set('rol', usuario_a_registrar.rol)      

        // let jsonUsuarioRegistro = JSON.stringify(body); 
        // let params = jsonUsuarioRegistro;

        // console.log({bodyPrueba: body, message: "Vemos el params"});
        console.log({dataPrueba: data, message: "Vemos el params"});

        // Como usamos Js en el servidor tambien, definimos asi el Content-Type
        // let headers = new HttpHeaders({
        //     'Content-Type':'application/json',
        //     'autorizacion':'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJzdWIiOiI2NDk2MGVkNWE4ZDA3OGY0NWY3YjAyNDAiLCJub21icmVfdXN1YXJpbyI6Ik1hbnVVc2VyIiwiZW1haWwiOiJtYW51cGVyZXpAZ21haWwuY29tIiwicm9sIjoiQ2xpZW50ZSIsImltYWdlbiI6Im51bGwiLCJpYXQiOjE2OTE1MzE2MDEsImV4cCI6MTY5NDEyMzYwMX0.i0fL37ACEC2I_oWrwPDGfXrHGK2eDCfnarXK17hAgmA',
        //     'Authorization': 'Bearer clave_secreta_trabajo_final'
        // });

        // const headers = new HttpHeaders({
        //     'Content-Type': 'application/x-www-form-urlencoded'
        // });

        let headers = new HttpHeaders({
            'X-Powered-By':'Express',
            'Access-Control-Allow-Origin':'*',
            'Access-Control-Allow-Headers':'Authorization, X-API-KEY, Origin, X-Requested-With, Content-Type, Accept, Access-Control-Allow-Request-Method',
            'Access-Control-Allow-Methods':'GET, POST, OPTIONS, PUT, DELETE',
            'Allow':'GET, POST, OPTIONS, PUT, DELETE',                
            //'Content-Type':'application/json; charset=utf-8',
            'Content-Length':'170',
            'ETag':'W/"aa-dEk0XYPzO0cVqB4cftPj7FRp/Uc"',
            'Date':'Thu, 10 Aug 2023 19:11:39 GMT',
            'Connection':'keep-alive',
            'Keep-Alive':'timeout=5',
            //'Authorization': 'Bearer clave_secreta_trabajo_final'
            'Content-Type': 'application/x-www-form-urlencoded'
        });

        console.log({ message: "Antes del post de guardar-persona "});     
        console.log(await this._http.post( this.url + 'guardar-persona', jsonUsuarioRegistro, {headers: headers} ));
        debugger;

        try {
            // Devolvemos la petición AJAX            
            const resp = await this._http.post( this.url + 'guardar-persona', jsonUsuarioRegistro, {headers: headers} )
            .pipe(                
                map(response => {
                    debugger;
                    console.log( {resp: response, message: "Respuesta dentro del pipe.map..."} );
                    //return response;
                }),
                // Error arrojado desde el usuario.js. Clave incorrecta, usuario incorrecto, etc..
                catchError(error => { 
                    debugger;
                    console.log({error:error.error.message, message: "No se encontró el usuario logueado"});
                    
                    // Puedo definir si solo mando eso, o el status y mas info o solo el "error"
                    throw error.error.message;
                }));     

            console.log({ message: "No tira error?", respuesta: resp });
            return resp;

        } catch (error) {
            console.log({error:error, message: "CATCH -No se encontró el usuario logueado"});
            
            // Puedo definir si solo mando eso, o el status y mas info o solo el "error"
            throw error;
        } 
        

    }    
}
